mapper.select:
	SELECT ANY_VALUE(po.optionid), ANY_VALUE(po.itemid), ANY_VALUE(it.name) AS item_name, po.color, ANY_VALUE(po.size), ANY_VALUE(po.stock) FROM product_option po
   INNER JOIN item it ON po.itemid = it.itemid
   WHERE po.itemid=#{itemid}
   GROUP BY po.color;

   SELECT po.size FROM product_option po
	INNER JOIN item it ON po.itemid = it.itemid
	WHERE po.itemid=#{itemid}
	GROUP BY po.size;
